import "./Auth.css"
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

export const Login = () => {
    const username = useRef()
    const password = useRef()
    const invalidDialog = useRef()
    const navigate = useNavigate()

    const handleLogin = (e) => {
        e.preventDefault()
        const user = {
